import React, { useState, useEffect } from 'react';
import TodoList from "./TodoList.js";
import './App.css';

function App() {
    const [todos, setTodos] = useState([]);
    const [todoInput, setTodoInput] = useState('');

    useEffect(() => {
        fetch('/api/todos')
            .then(response => response.json())
            .then(data => setTodos(data))
            .catch(error => console.error('Erro ao buscar todos:', error));
    }, []);

    const handleInputChange = (e) => {
        setTodoInput(e.target.value);
    };

    const handleAddTodo = () => {
        if (todoInput.trim() !== '') {
            const newTodo = { text: todoInput, completed: false };

            fetch('/api/todos', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newTodo)
            })
                .then(response => response.json())
                .then(savedTodo => setTodos([...todos, savedTodo]))
                .catch(error => console.error('Erro ao adicionar todo:', error));

            setTodoInput('');
        }
    };

    const handleToggleTodo = (id) => {
        const todo = todos.find(todo => todo.id === id);
        const updatedTodo = { ...todo, completed: !todo.completed };

        fetch(`/api/todos/${id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(updatedTodo)
        })
            .then(response => response.json())
            .then(savedTodo => setTodos(todos.map(todo => (todo.id === id ? savedTodo : todo))))
            .catch(error => console.error('Erro ao atualizar todo:', error));
    };

    const handleDeleteTodo = (id) => {
        fetch(`/api/todos/${id}`, {
            method: 'DELETE'
        })
            .then(() => setTodos(todos.filter(todo => todo.id !== id)))
            .catch(error => console.error('Erro ao deletar todo:', error));
    };

    return (
        <div className="bodyPage">
            <div className="window">
                <div className="titleWindow">
                    <h1>Marombeiro Raiz</h1>
                </div>
                <div className="bodyWindow">
                    <h2>Lista de Exerc√≠cios</h2>
                    <input type="text" value={todoInput} onChange={handleInputChange} />
                    <div className="seuBotao">
                        <button onClick={handleAddTodo}>Adicionar</button>
                        <TodoList todos={todos} onToggleTodo={handleToggleTodo} onDeleteTodo={handleDeleteTodo} />
                        <button onClick={handleAddTodo}>Salvar Lista</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
